import React, { useEffect,useState } from 'react';
import '../assets/css/style.css';
import Footer from '../components/Footer';
import Button from '../components/Button';
import HeaderProfil from '../components/HeaderProfil';
import '../assets/fontawesome-5/css/all.css';
import WelcomeContainer from '../components/WelcomeContainer';
import SideCardContainer from '../components/SideCardContainer';
import Card from '../components/Card';
import CardDetails from '../components/CardDetails';
import {getUserFirstTerrain,getUserCulture,deconnexion} from '../assets/js/Function';

const Historique = () => {
    const [userData, setUserData] = useState(null);
    const [cultureData, setCultureData] = useState(null);
    const [terrainData, setTerrainData] = useState(null);

    useEffect(() => {
        document.title = 'Historique des simulations';
        const userDataString = localStorage.getItem('userData');
        const parsedUserData = JSON.parse(userDataString);
        setUserData(parsedUserData);

        const fetchData = async () => {
            try {
                const userCultureData = await getUserCulture(parsedUserData[0].id);
                setCultureData(userCultureData);
                const userTerrainData = await getUserFirstTerrain(parsedUserData[0].id);
                setTerrainData(userTerrainData);
            } catch (error) {
                console.error('Error fetching user culture:', error);
            }
        };

        fetchData();
    }, []);

    // console.log('User Data:', userData);

    if (!userData) {
        return null;
    }

    return (
        <div className='page'>
            <HeaderProfil link='/accueilFront' logo = "/assets/img/PNG/Logo.png" description = "Logo" icon = 'fas fa-user-circle' pseudo = {userData[0].pseudo} lien1 = '/ficheProfil' text1 = 'Voir ma fiche' lien2 = {deconnexion} text2 = 'Se dÃ©connecter'/>
            <main className='Landing'>
                <section className='MidLeft2'>
                    <h1 className='title2'>Historique des simulations</h1>
                    <p className='desc2'>
                        Voici l'historique de vos simulations.
                    </p>
                    <table>
                        <tr>
                            <th>Culture</th>
                            <th>Type</th>
                            <th>Saison</th>
                            <th>Prix d'achat(en Ar)</th>
                            <th>Prix de vente(en Ar)</th>
                            <th>PropriÃ©taire</th>
                        </tr>
                    </table>
                </section>
                <section className='MidRight2'>
                    <aside>
                        <form method='get'>
                            <h1>Filtrer par :</h1>
                            <p>
                                Parcelle :{' '}
                                <select
                                    name='selectedParcelle'
                                    id='p1'
                                >
                                    <option value='Tous'>Tous</option>
                                </select>
                            </p>
                            <Button text='Valider' />
                        </form>
                    </aside>
                </section>
            </main>
            <Footer copyright = "Â© Tous droits rÃ©servÃ©s. Farm Game 2024"></Footer>
        </div>
    );
};

export default Historique;
